import React, {Component} from 'react';
import './Cadastro.css';
import Header from './componentes/Header/index.js'
import TituloNovoPost from './componentes/NovoPost/TituloNovoPost/index.js'
import ConteudoNovoPost from './componentes/NovoPost/ConteudoNovoPost';

class Cadastro extends Component {

  constructor(props){
    super(props);

    this.state = {
      res: [],
      pag: 1
    };

    this.componentDidMount = this.componentDidMount.bind(this);
    this.callApi = this.callApi.bind(this);
  }


  componentDidMount() {
    this.callApi()
    .then(resp => this.setState({
      res: resp

    }))
    .catch(err => console.log(err));
  }

  callApi = async () => {
    const response = await fetch(`http://localhost:3001/api/Postagens?page=${this.state.pag}`);
    const body = await response.json();
    if (response.status !== 200) throw Error(body.message);

    return body;
  };

  setPag = async (pag) => {
    await this.setState({pag: pag});

    this.componentDidMount();
  }



  render() {

    if(this.state.res.length === 0){
      return "";
    }

    console.log(this.state);

    return (
        <div className="Cadastro">
          <Header></Header>
          <TituloNovoPost titulo="NOVA PUBLICAÇÃO"></TituloNovoPost>
          <ConteudoNovoPost></ConteudoNovoPost>
       </div>


    );
  }
}

export default Cadastro;
