import React, {Component} from 'react'
import './style.css'
import TextField from '@material-ui/core/TextField';
import { makeStyles } from '@material-ui/core/styles';
import Button from '@material-ui/core/Button';

import BotoesNovoPost from './BotoesNovoPost/index'




export default class ConteudoNovoPost extends Component{


    constructor(props){
        super(props);

        this.state = {
            novoTitulo: "",
            Titulo: ""
        }
    }



    setConteudo = async (e) => {
        await this.setState({novoTitulo : e.target.value});
        console.log("DDD: " + this.novoTitulo + " = " + e.target.value);

    }

    setConteudoFinal = (e) => {
        console.log("DDDS: " + this.novoTitulo);
        this.setState({Titulo : this.novoTitulo});

    }

    render(){
        return(
                <div className="conteudoNovoPost">
                    <form onSubmit={this.setConteudoFinal}>
                    <TextField
                        className="txt1"
                        label="Titulo" 
                        fullWidth
                        margin="normal"
                        value={this.state.novoTitulo}
                        onChange={this.setConteudo}
                        InputLabelProps={{
                            shrink: true,
                        }}>

                    </TextField>

                    <TextField
                        className="txt2"
                        id="standard-full-width"
                        label="ConteÃºdo"
                        multiline={true}
                        rows={10}
                        rowsMax={10}   
                        fullWidth
                        margin="normal"
                        InputLabelProps={{
                            shrink: true,
                        }}>

                    </TextField>

                    <BotoesNovoPost onSubmit={this.setConteudoFinal}></BotoesNovoPost>

                    </form>
                </div> 
        );
    }
}
