import React, {Component} from 'react'
import './style.css'
import TextField from '@material-ui/core/TextField';
import { makeStyles } from '@material-ui/core/styles';
import Button from '@material-ui/core/Button';
import { Link } from 'react-router-dom';

import BotoesNovoPost from './BotoesNovoPost/index'




export default class ConteudoNovoPost extends Component{


    constructor(props){
        super(props);

        this.state = {
            novoTitulo: "",
            Titulo: "",
            Conteudo: "",
            novoConteudo: ""
        }

        this.setTitulo = this.setTitulo.bind(this);
        this.setValorFinal = this.setValorFinal.bind(this);
        this.setConteudo = this.setConteudo.bind(this);

    }



    setTitulo(e) {
        this.setState({novoTitulo : e.target.value});

     }

     setConteudo(e) {
         this.setState({novoConteudo : e.target.value});
     }

     setValorFinal(e) {
         this.setState({Titulo : this.state.novoTitulo, Conteudo: this.state.novoConteudo});




         e.preventDefault();

     }

    render(){
        return(
                <div className="conteudoNovoPost">
                    <form action="http://localhost:3001/api/Postagens/add" method="post" onSubmit={this.setValorFinal} >
                    <TextField
                        className="txt1"
                        label="Titulo" 
                        fullWidth
                        margin="normal"
                        value={this.state.novoTitulo}
                        onChange={this.setTitulo}
                        InputLabelProps={{
                            shrink: true,
                        }}>

                    </TextField>

                    <TextField
                        className="txt2"
                        id="standard-full-width"
                        label="ConteÃºdo"
                        multiline={true}
                        rows={10}
                        rowsMax={10}   
                        value={this.state.novoConteudo}
                        onChange={this.setConteudo}
                        fullWidth
                        margin="normal"
                        InputLabelProps={{
                            shrink: true,
                        }}>

                    </TextField>

                    <BotoesNovoPost></BotoesNovoPost>

                    <h1>{this.state.Titulo}</h1>
                    <h1>{this.state.Conteudo}</h1>


                    </form>


                    <form action="http://localhost:3001/api/Postagens" method="POST">
                        <label>Titulo</label><br/>
                        <input type="text" id="titulo" name="titulo"></input><br/>
                        <label>Conteudo</label><br/>
                        <textarea type="text" rows="10" id="conteudo" name="conteudo"></textarea><br/>
                        <input type="submit" value="Salvar"></input><br/>
                        <Link to="/">
                        <button>
                            Cancelar
                            </button>
                    </Link>
                    </form>

                </div> 
        );
    }
}
