import React from 'react';
import './App.css';
import Header from './componentes/Header/index.js'
import Titulo from './componentes/Titulo/index.js'
import PublicacoesArea from './componentes/PublicacoesArea/index.js'
import Menu from './componentes/Menu/index.js'

class App extends Component {
  state = {
    response: ''
  };

  componentDidMount() {
    this.callApi()
      .then(res => this.setState({ response: res.express }))
      .catch(err => console.log(err));
  }

  callApi = async () => {
    const response = await fetch('/api');
    const body = await response.json();
    if (response.status !== 200) throw Error(body.message);

    return body;
  };

  render() {
    return (
        <div className="App">
          <Header></Header>
          <Titulo></Titulo>
          <div className="lado-lado">
            <div className="area1">
              <PublicacoesArea></PublicacoesArea>
            </div>
            <div className="area2">
              <Menu></Menu>
            </div>
          </div>
          <p className="App-intro">{this.state.response}</p>
       </div>


    );
  }
}

export default App;
