import React, {Component} from 'react';
import './App.css';
import Header from './componentes/Header/index.js'
import Titulo from './componentes/Titulo/index.js'
import PublicacoesArea from './componentes/PublicacoesArea/index.js'
import Menu from './componentes/Menu/index.js'

class App extends Component {

  constructor(props){
    super(props);

    this.state = {
      docs: [{
        _id: 0,
        titulo: "",
        conteudo: "",
        data: "",

      }]
    };

    this.componentDidMount = this.componentDidMount.bind(this);
    this.callApi = this.callApi.bind(this);
  }


  componentDidMount() {
    this.callApi()

      .then(res => this.setState({ docs: res }))
      .catch(err => console.log(err));


  }

  callApi = async () => {
    const response = await fetch('http://localhost:3001/api/Postagens');
    const body = await response.json();
    if (response.status !== 200) throw Error(body.message);

    return body;
  };

  render() {
    return (
        <div className="App">
          <Header></Header>
          <Titulo></Titulo>
          <div className="lado-lado">
            <div className="area1">
              <PublicacoesArea {...this.state.docs}></PublicacoesArea>
              <p className="App-intro">{this.state.docs}</p>
            </div>
            <div className="area2">
              <Menu></Menu>
            </div>
          </div>
       </div>


    );
  }
}

export default App;
