import React, {Component} from 'react'
import './style.css'
import Moment from 'moment'
import ClearIcon from '@material-ui/icons/Clear';
import CreateIcon from '@material-ui/icons/Create';
import { Link } from 'react-router-dom'
import { throws } from 'assert';





export default class Publicacoes extends Component{

    constructor(props){
        super(props);

        this.SetTeste = this.SetTeste.bind(this);
    }

    async SetTeste(){
        await fetch(`http://localhost:3001/api/Deletar/${this.props.post._id}`);


    }


    render(){

        const newTo = { 
            pathname: "/Editar/" + this.props.post._id,
            param1: this.props.post.titulo,
            param2: this.props.post.conteudo

          };

          const newTo2 = { 
            pathname: "http://localhost:3001/api/Deletar/" + this.props.post._id        
          };



        console.log(this.props.post);

        return(
            <div className="post">
                <div className="barra">
                    <h3>{this.props.post.titulo}</h3> 
                <div>
                    <form action="http://localhost:3001/api/Refresh" method="post">


                        <button type="submit" className="btn" onClick={this.SetTeste}>
                            <ClearIcon></ClearIcon>
                        </button>
                    </form>
                    <Link to={newTo} className="btnEditar"><CreateIcon/></Link>

                </div>

                </div>

                <div className="conteudo">
                    <div className="data">
                        <p>{Moment(this.props.post.data).format("DD/MM/YYYY hh:mm")}</p>
                    </div>

                    <div className="descricao">
                            <p>{this.props.post.conteudo}</p>
                    </div>
                </div>
            </div>
        );
    }
}
