import React, {Component} from 'react';


class ConteudoMenu extends Component {

  constructor(props){
    super(props);

    this.state = {
      resRecentes: []
    };

    this.componentDidMount = this.componentDidMount.bind(this);
    this.callApi = this.callApi.bind(this);
  }


  componentDidMount() {

    //Consulta 5 mais recentes
    this.callApi()
    .then(resp => this.setState({
      resRecentes: resp

    }))
    .catch(err => console.log(err));
  }

  //Consulta 5 mais recentes
  callApi = async () => {
    console.log("AAA" + this.state.pag);
    const response = await fetch(`http://localhost:3001/api/PostagensRecentes`);
    const body = await response.json();
    if (response.status !== 200) throw Error(body.message);

    return body;
  };


  render() {

    if(this.state.resRecentes.length === 0){
      return "";
    }

    console.log(this.state);

    return (
        <div className="ConteudoMenu">
          <ul>
              <li>{this.state.resRecentes[0].titulo}</li>
              <li>{this.state.resRecentes[1].titulo}</li>
              <li>{this.state.resRecentes[2].titulo}</li>
              <li>{this.state.resRecentes[3].titulo}</li>
              <li>
                {this.setTopico(4)}
              </li>
          </ul>

       </div>


    );
  }
}

export default ConteudoMenu;
