import React, {Component} from 'react';
import './App.css';
import Header from './componentes/Header/index.js'
import Titulo from './componentes/Titulo/index.js'
import PublicacoesArea from './componentes/PublicacoesArea/index.js'
import Menu from './componentes/Menu/index.js'

class App extends Component {

  constructor(props){
    super(props);

    this.state = {
      res: []
    };

    this.componentDidMount = this.componentDidMount.bind(this);
    this.callApi = this.callApi.bind(this);
  }


  componentDidMount() {
    this.callApi()
    .then(resp => this.setState({
      res: resp

    }))
    .catch(err => console.log(err));
  }

  callApi = async (pagina = 1) => {
    const response = await fetch(`http://localhost:3001/api/Postagens?page=${pagina}`);
    const body = await response.json();
    if (response.status !== 200) throw Error(body.message);

    return body;
  };

  render() {

    if(this.state.res.length === 0){
      return 0;
    }

    console.log(this.state);

    return (
        <div className="App">
          <Header></Header>
          <Titulo></Titulo>
          <div className="lado-lado">
            <div className="area1">
              <PublicacoesArea post={this.state.res}></PublicacoesArea>
              <p className="App-intro">{}</p>
            </div>
            <div className="area2">
              <Menu></Menu>
            </div>
          </div>
       </div>


    );
  }
}

export default App;
