import React, {Component} from 'react';
import './App.css';
import Header from './componentes/Header/index.js'
import Titulo from './componentes/Titulo/index.js'
import PublicacoesArea from './componentes/PublicacoesArea/index.js'
import Menu from './componentes/Menu/index.js'

class App extends Component {

  constructor(props){
    super(props);

    this.state = {
      response: ''
    };

    this.componentDidMount = this.componentDidMount.bind(this);
    this.callApi = this.callApi.bind(this);
  }


  componentDidMount() {
    this.callApi()
      .then(res => this.setState({ response: res.express.titulo }))
      .catch(err => console.log(err));
  }

  callApi = async () => {
    const response = await fetch('/api');
    const body = await response.json();
    if (response.status !== 200) throw Error(body.message);

    return body;
  };

  render() {
    return (
        <div className="App">
          <Header></Header>
          <Titulo></Titulo>
          <div className="lado-lado">
            <div className="area1">
              <PublicacoesArea></PublicacoesArea>
              <p className="App-intro">{this.state.response}</p>
            </div>
            <div className="area2">
              <Menu></Menu>
            </div>
          </div>
       </div>


    );
  }
}

export default App;
