import React, {Component} from 'react'
import './style.css'
import TextField from '@material-ui/core/TextField';
import { makeStyles } from '@material-ui/core/styles';
import Button from '@material-ui/core/Button';
import { Link } from 'react-router-dom';




export default class ConteudoNovoPost extends Component{


    constructor(props){
        super(props);

        this.state = {
            novoTitulo: "",
            Titulo: ""
        }
    }



    setConteudo = (e) => {
        this.setState({novoTitulo : e.target.value});
    }

    setConteudoFinal = (e) => {
        this.setState({Titulo : this.novoTitulo});
    }

    render(){
        return(
                <div className="conteudoNovoPost">
                    <form onSubmit={this.setConteudoFinal}>
                    <TextField
                        className="txt1"
                        label="Titulo" 
                        fullWidth
                        margin="normal"
                        InputLabelProps={{
                            shrink: true,
                        }}>
                            <input onChange={this.setConteudo}></input>
                    </TextField>

                    <TextField
                        className="txt2"
                        id="standard-full-width"
                        label="ConteÃºdo"
                        multiline={true}
                        rows={10}
                        rowsMax={10}   
                        fullWidth
                        margin="normal"
                        InputLabelProps={{
                            shrink: true,
                        }}>
                            <input></input>
                    </TextField>

                    <Button variant="contained" color="primary" className="btn1">
                        Salvar
                    </Button>
                    <Link to="/">
                        <Button variant="contained" marginLeft={50} className="btn2">
                            Cancelar
                        </Button>
                    </Link>

                    </form>
                </div> 
        );
    }
}
